import React from 'react';
import {render} from 'react-dom';
import {BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';

import { Welcome } from './pages/welcome.js';
import {Quiz} from './pages/quiz.js'

function AppWrapper() {
    const navigate = useNavigate();
  
    return <App navigate={navigate} />;
  }
  


class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            results: {
                amountCorrect: 0,
                numberOfQuestions: 0,
            }
        }
        this.endOfQuestions = this.endOfQuestions.bind(this);
    }

    endOfQuestions(howManyCorrect, howManyQuestions){
        console.log('End');
        this.setState((state)=>{
            return {
                amountCorrect: howManyCorrect,
                numberOfQuestions: howManyQuestions
            }
        });
    }

    render(){
        return (
            <Router>
                {this.state.redirect && <redirect to="/results" />}
                <div></div>
                <Routes>
                    <Route path="/results"/>
                    <Route path="/" element={<Welcome />} />
                    <Route path="/quiz" element={<Quiz endOfQuestions={this.endOfQuestions}/>}/>
                </Routes>
            </Router>
        )
    }
}

render(<App />, document.getElementById('root'));
