import React from 'react';

import Question from '../components/question.js';

export class Quiz extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            questions: [{q: 'What is JSX?',
                        choices: ['a) A standalone programming language', 'b) A CSS framework', 'c) A syntactic extension of JavaScript that looks like HTML', 'd) A templating engine'],
                        indexCorrect: 2
                        }, 
                    
                    
                    ],
            amountCorrect: 0,
            questionNumber: 0
        }
        this.answeredCorrect = this.answeredCorrect.bind(this);
    }
     answeredCorrect(){
         console.log('Answered Correct');
         this.setState((state) => {
            return { amountCorrect: state.amountCorrect + 1 };
        });
         console.log(this.state);
     }

    render(){
        return (
            <div>
                <Question answeredCorrect={this.answeredCorrect} question={this.state.questions[this.state.questionNumber]}/>

            </div>
        )
    }
}
